mapper.select:
  select 
  
  from a_customer
 
 
 
  id = #{id}
 
 
 
 AND status = #{status}
 
 
 
 AND approve_status = #{approveStatus}
 
 
  
 AND mobile = #{mobile}
 
 
  
 AND city = #{city}
 
 
  
 AND is_delete = #{isDelete}
 
 
 
 AND shop_name  LIKE CONCAT(CONCAT('%', #{shopName}), '%')
 
 
ORDER BY gmt_create DESC LIMIT #{page.start}, #{page.pageItemCount}
